import React, { useEffect } from "react";
import "./Hex.css";

const Hexagon = () => {
  useEffect(() => {
    let mousePosition = { x: 0, y: 0 };
    document.addEventListener("mousemove", (mouse) => {
      mousePosition = { x: mouse.clientX, y: mouse.clientY };
    });

    const loop = () => {
      const gradientElement = document.getElementById("gradient");
      if (gradientElement) {
        gradientElement.style.transform = `translate(${mousePosition.x}px, ${mousePosition.y}px)`;
      }
      window.requestAnimationFrame(loop);
    };
    window.requestAnimationFrame(loop);

    const patternElement = document.getElementById("pattern");
    if (patternElement) {
      const countY = Math.ceil(patternElement.clientHeight / 40) + 1;
      const countX = Math.ceil(patternElement.clientWidth / 48) + 1;
      for (let i = 0; i < countY; i++) {
        for (let j = 0; j < countX; j++) {
          const hexagon = document.createElement("div");
          hexagon.style = `
            background: url('data: image/svg+xml; base64, PHN2ZyB3aWR®aD01
ODciIGhlaWdodDĐiMTAwIiB2aWV3Qm94PSIwIDAg0DcgMTA
wIiBmaWxsPSJub251IiB4bWxucz0iaHR0cDovL3d3dy53My
5vcmcvMjAwMC9zdmciPgo8cGF0aCBPSJNMi4x0Tg3MyAyN
14xNTQ3TDQzLjUgMi4zMDk0TDgOLjgwMMgMj YuMTUON1Y3
My44NDUzTDQzLjUgOTcuNj kwNkwyLjE50DczIDczLjgONTN
WMjYuMTU0N1oiIGZpbGw9IiMxMzEyMTciIHN0cm9rZT0iIz
EzMTIxNyIgc3RybtLLXdpZHRoPSI0Ii8+Cjwvc3ZnPg0='
) no-repeat;;
            width: 44px;
            height: 50px;
            background-size: contain;
            position: absolute;
            top: ${i * 40}px;
            left: ${j * 48 + ((i * 24) % 48)}px;
          `;
          patternElement.appendChild(hexagon);
        }
      }
    }
  }, []);

  return (
    <>
      <h1>Hexagons</h1>
      <div id="pattern"></div>
      <div id="gradient"></div>
    </>
  );
};

export default Hexagon;
